mapper.insert:

	SELECT SEQ_BOARD_NO.NEXTVAL FROM DUAL


INSERT INTO BOARD(
 BOARD_NO
,MEMBER_NO
,BOARD_TITLE
,BOARD_DATE
,BOARD_CONTENT
,BOARD_HIT_COUNT
,BOARD_LIKE_COUNT
,BOARD_REPORT_COUNT
,BOARD_GROUP_NO
,BOARD_TAG_NAME
)
VALUES(
 #{board_no}
,#{member_no}
,#{board_title}
,SYSDATE
,#{board_content}
,0
,0
,0
,#{board_group_no}
,#{board_tag_name}
)

mapper.update:
UPDATE BOARD SET
BOARD_FILE_CHECK = 'Y'
WHERE BOARD_NO = #{board_no}

