mapper.update:
UPDATE BOARD
SET
 BOARD_TITLE = #{board_title}
 ,BOARD_CONTENT = #{board_content}
 ,BOARD_GROUP_NO = #{board_group_no}
 ,BOARD_TAG_NAME = #{board_tag_name}
WHERE BOARD_NO = #{board_no} AND MEMBER_NO = #{member_no}

mapper.select:
SELECT NVL(MAX(REF_LEVEL),0) FROM BCOMMENT
WHERE BOARD_NO  = #{board_no}
AND REF_STEP = #{ref_step}

mapper.insert:

	SELECT SEQ_BCOMMENT_NO.NEXTVAL FROM DUAL


INSERT INTO BCOMMENT
VALUES(
 #{bcomment_no}
,#{board_no}
,#{bcomment_content}
,SYSDATE
,#{member_no}
,0
,0
,#{ref_step}
,#{ref_level}
)

mapper.update:
UPDATE  BOARD SET 
BOARD_TAG_NAME = ''
WHERE BOARD_NO = #{board_no}

UPDATE BCOMMENT
SET
BCOMMENT_DATE = SYSDATE,
BCOMMENT_CONTENT = #{bcomment_content}
WHERE 
BCOMMENT_NO = #{bcomment_no}

UPDATE BCOMMENT
SET
BCOMMENT_CONTENT = #{bcomment_content},
BCOMMENT_DATE = SYSDATE
WHERE
BOARD_NO = #{board_no}
AND
REF_STEP = #{ref_step}
AND
REF_LEVEL = #{ref_level}
