mapper.select:
SELECT B.BOARD_NO ,B.BOARD_GROUP_NO ,B.BOARD_TAG_NAME ,B.BOARD_TITLE ,B.BOARD_DATE ,B.BOARD_HIT_COUNT ,B.BOARD_LIKE_COUNT ,B.BOARD_REPORT_COUNT ,B.BOARD_FILE_CHECK ,G.BGROUP_NAME ,M.MEMBER_NICK ,M.MEMBER_NO
FROM BOARD B INNER JOIN BGROUP G ON B.BOARD_GROUP_NO = G.BOARD_GROUP_NO INNER JOIN MEMBER M ON B.MEMBER_NO = M.MEMBER_NO
WHERE B.MEMBER_NO = #{member_no}

mapper.insert:
INSERT INTO FOLLOW(
 FOLLOW_DATE
,FOLLOW_NO
,FOLLOWING_MEMBER_NO
,MEMBER_NICK
)
VALUES(
SYSDATE
,#{follow_no}
,#{member_no}
,#{member_nick}
)
