mapper.select:
SELECT MEMBER_NAME, MEMBER_ID, MEMBER_EMAIL,
MEMBER_DATE, MEMBER_NICK, MEMBER_POINT, MEMBER_PROFILE, MEMBER_PRO_CHECK, MEMBER_ADMIN FROM MEMBER
WHERE MEMBER_NO = #{member_no}

SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_PW = #{member_pw}

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME = #{member_name}
AND MEMBER_EMAIL = #{member_email}

SELECT MEMBER_PW
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_EMAIL = #{member_email}

mapper.insert:
INSERT INTO MEMBER(
 MEMBER_NO
,MEMBER_ID
,MEMBER_PW
,MEMBER_NAME
,MEMBER_EMAIL
,MEMBER_NICK
,MEMBER_DATE
,MEMBER_POINT
,MEMBER_PRO_CHECK
)
VALUES(
(SELECT NVL(MAX(MEMBER_NO),0)+1 FROM MEMBER)
,#{member_id}
,#{member_pw}
,#{member_name}
,#{member_email}
,#{member_nick}
,sysdate
,0
,'N'
)

mapper.update:
UPDATE MEMBER SET
 MEMBER_PW = #{member_pw}
,MEMBER_EMAIL = #{member_email}
,MEMBER_PRO_CHECK = 'N'
 WHERE MEMBER_NO = #{member_no}

UPDATE MEMBER SET
 MEMBER_PW = #{member_pw}
 WHERE MEMBER_EMAIL = #{member_email}
 AND MEMBER_ID = #{member_id}

mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_NO = #{member_no}
AND MEMBER_PW = #{member_pw}

mapper.update:
UPDATE chat SET
RECV_NICK = '탈퇴한 회원'
WHERE RECV_NICK = #{member_nick}

UPDATE chat SET
SEND_NICK = '탈퇴한 회원'
WHERE SEND_NICK = #{member_nick}
