mapper.select:
SELECT MEMBER_NAME, MEMBER_ID, MEMBER_EMAIL,
MEMBER_DATE, MEMBER_NICK, MEMBER_POINT FROM MEMBER
WHERE MEMBER_NO = #{member_no}

SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_PW = #{member_pw}

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME = #{member_name}
AND MEMBER_EMAIL = #{member_email}

SELECT MEMBER_PW
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_EMAIL = #{member_email}

mapper.insert:
INSERT INTO MEMBER(
 MEMBER_NO
,MEMBER_ID
,MEMBER_PW
,MEMBER_NAME
,MEMBER_EMAIL
,MEMBER_NICK
,MEMBER_DATE
,MEMBER_POINT
,MEMBER_PRO_CHECK
)
VALUES(
SEQ_MEMBER_NO.nextval
,#{member_id}
,#{member_pw}
,#{member_name}
,#{member_email}
,#{member_nick}
,sysdate
,0
,#{member_pro_check}
)

mapper.update:
UPDATE MEMBER SET
 MEMBER_PW = #{member_pw}
,MEMBER_EMAIL = #{member_email}
,MEMBER_PRO_CHECK = #{member_pro_check}
 WHERE MEMBER_NO = #{member_no}

mapper.delete:
DELETE FROM MEMBER 
WHERE MEMBER_NO = #{member_no} 
AND MEMBER_PW = #{member_pw}

mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_NO = #{member_no}
AND MEMBER_PW = #{member_pw}
