mapper.select:
SELECT RN, MEMBER_NO, MEMBER_ID, MEMBER_NAME, MEMBER_NICK, MEMBER_EMAIL, MEMBER_DATE, MEMBER_ADMIN, MEMBER_PRO_CHECK FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT MEMBER_NO, MEMBER_ID, MEMBER_NAME, MEMBER_NICK, MEMBER_EMAIL, MEMBER_DATE, MEMBER_ADMIN, MEMBER_PRO_CHECK FROM MEMBER
WHERE MEMBER_ID LIKE '%'||#{keyword}||'%'
WHERE MEMBER_NICK LIKE '%'||#{keyword}||'%'
WHERE MEMBER_ID LIKE '%'||#{keyword}||'%' OR MEMBER_NICK LIKE '%'||#{keyword}||'%'
) A ) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:

UPDATE MEMBER SET
MEMBER_NICK = #{member_nick},
MEMBER_ADMIN = #{member_admin},
MEMBER_PRO_CHECK = #{member_pro_check}
WHERE MEMBER_NO = #{member_no}

UPDATE NOTICE
SET NOTI_TITLE = #{noti_title},
	NOTI_CONTENT = #{noti_content}
WHERE NOTI_NO = #{noti_no}

UPDATE NOTICE SET
NOTI_FILE_CHECK = 'Y'
WHERE NOTI_NO = #{noti_no}

mapper.insert:
 INSERT INTO BFILE(
 FILE_NO
,NOTI_NO
,ORG_FILE_NAME
,STORED_FILE_NAME
,FILE_SIZE
,FILE_DATE
)
VALUES(
 SEQ_FILE_NO.NEXTVAL
,#{NOTI_NO}
,#{ORG_FILE_NAME}
,#{STORED_FILE_NAME}
,#{FILE_SIZE}
,#{FILE_DATE}
)

mapper.update:

	UPDATE BFILE SET
	DEL_GB = 'Y'
	WHERE FILE_NO = #{FILE_NO}
