mapper.select:
SELECT RN, MEMBER_NO, MEMBER_ID, MEMBER_NAME, MEMBER_NICK, MEMBER_EMAIL, MEMBER_DATE, MEMBER_PRO_CHECK FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT MEMBER_NO, MEMBER_ID, MEMBER_NAME, MEMBER_NICK, MEMBER_EMAIL, MEMBER_DATE, MEMBER_PRO_CHECK FROM MEMBER
WHERE MEMBER_ID LIKE '%'||#{keyword}||'%'
WHERE MEMBER_NICK LIKE '%'||#{keyword}||'%'
WHERE MEMBER_ID LIKE '%'||#{keyword}||'%' OR MEMBER_NICK LIKE '%'||#{keyword}||'%'
ORDER BY MEMBER_NO DESC
) A ) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:

UPDATE MEMBER SET
MEMBER_PW = #{member_pw},
MEMBER_NICK = #{member_nick}
WHERE MEMBER_NO = #{member_no}
