mapper.select:
SELECT MEMBER_ID, MEMBER_PW, MEMBER_NO
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_PW = #{member_pw}

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME = #{member_name}
AND MEMBER_EMAIL = #{member_email}

SELECT MEMBER_PW
FROM MEMBER
WHERE MEMBER_ID = #{member_id}
AND MEMBER_EMAIL = #{member_email}
